import{_ as s,o as t,c as a,e as p}from"./chunks/framework.mYMQfzZG.js";const _="/assets/0.UCmE0u9I.jpg",r="/assets/13423757_1218334921518718_7686250611240665682_n.ZJUraBCe.jpg",o="/assets/13522713_1230620343623509_2310412999472314445_o.MCbD1b8F.jpg",c="/assets/13557851_1232447293440814_345281135122762471_n.DSmF8N8m.jpg",i="/assets/14711459_1324080514277491_3083106037309928711_o.x2yG2Wud.jpg",e="/assets/3de0Qk-B3h4.2F5nxRCc.jpg",m="/assets/8YC3WHjOdY0.YCMLh_6M.jpg",g="/assets/IMG_3664.X8fTeiTy.jpg",l="/assets/IMG_3671.bDOM7USV.jpg",n="/assets/IMG_3675.RrtqW1w_.jpg",j="/assets/IMG_3677.y0Lay0mK.jpg",I="/assets/IMG_3681.z62DpuxH.jpg",G="/assets/IMG_3683.gg3qE-p7.jpg",M="/assets/IMG_3684.DVXpExqu.jpg",h="/assets/IMG_3696.BUsTtNfN.jpg",d="/assets/IMG_3700.ktY7w0z2.jpg",f="/assets/IMG_3914.6RxNVWEZ.jpg",u="/assets/ICBD4424.LcbMrLYD.jpg",b="/assets/IMG_4751.j3mpNzEk.jpg",k="/assets/IMG_4856.kbOBsWWb.jpg",x="/assets/13692416_1393280134018876_1137184485_o.h1SC3OZF.jpg",q="/assets/14215421_1432228223457400_514463506_o.TVWrtp0e.jpg",D="/assets/IMG_3805.rtCcewsC.jpg",P="/assets/IMG_3818.He5bFc7z.jpg",C="/assets/IMG_3820.Q4rGJele.jpg",w="/assets/IMG_3821.0lbWzHEs.jpg",E="/assets/IMG_5108.Wvp7AsHJ.jpg",T="/assets/IMG_5117.9EG5Nd4h.jpg",W="/assets/IMG_3823.qZcx9G9E.jpg",z="/assets/IMG_3824.XOsgWyTD.jpg",B="/assets/IMG_3826.RuJZ0d1K.jpg",Y="/assets/IMG_3829.VDXdfNky.jpg",H="/assets/IMG_3838.mnF4DMXQ.jpg",J="/assets/IMG_3906.UqRnrf4T.jpg",O="/assets/IMG_3876.kFsLKr9K.jpg",y="/assets/IMG_5178.bBpATRob.jpg",F="/assets/IMG_5284.tXw3kaf0.jpg",L="/assets/IMG_3942.USK5NYqL.jpg",Z="/assets/IMG_3943.ZRmJni0M.jpg",N="/assets/IMG_3946.N0gwdiXe.jpg",R="/assets/IMG_4169.er1fhjXg.jpg",S="/assets/IMG_4170.Z9SKawvO.jpg",V="/assets/IMG_4172.vfa98E9J.jpg",A="/assets/IMG_4176.ycQJhCSI.jpg",Q="/assets/IMG_4455.GPNcx579.jpg",X="/assets/IMG_6342.Ml8uc4Es.jpg",U="/assets/IMG_3841.Ws1qStT0.jpg",v="/assets/IMG_3911.vhfhk406.jpg",K="/assets/IMG_3913.fQuL7fxi.jpg",$="/assets/IMG_3920.SpYqIT4I.jpg",ss="/assets/IMG_3925.2knmbXkK.jpg",ts="/assets/IMG_3926.zlimyHId.jpg",as="/assets/IMG_4024.3gcrgspC.jpg",ps="/assets/IMG_4025.DTEprxeZ.jpg",_s="/assets/IMG_4036.uD8lK1zP.jpg",rs="/assets/IMG_4043.ZV56fw2n.jpg",os="/assets/IMG_4048.ZC76IH1t.jpg",cs="/assets/IMG_4164.W6bvitRK.jpg",is="/assets/13901829_10154380200950682_14045835_o.1UYuEJcd.jpg",es="/assets/IMG_5697.syeLIALx.jpg",ms="/assets/IMG_4015.2i1efOgR.jpg",gs="/assets/IMG_4016.cDjxjLMB.jpg",ls="/assets/IMG_4017.WBcfBDcu.jpg",ns="/assets/IMG_4018.1gzV9pxD.jpg",js="/assets/IMG_4022.WaCtrz5F.jpg",Is="/assets/IMG_5701.otAQPtVD.jpg",Gs="/assets/IMG_5704.0UKyBXdF.jpg",Ms="/assets/IMG_4085.wf8efuy2.jpg",hs="/assets/IMG_4086.-PUaje2_.jpg",ds="/assets/IMG_4096.lq6H3B9g.jpg",fs="/assets/IMG_4103.hTaJaDk7.jpg",us="/assets/IMG_4113.6tqq5Caw.jpg",bs="/assets/IMG_4116.LWVREbIT.jpg",ks="/assets/IMG_4118.DXQq8WBN.jpg",xs="/assets/IMG_4128.8DlzmUgG.jpg",qs="/assets/IMG_4130.oPdHoBfT.jpg",Ds="/assets/IMG_6041.mmJZlo6m.jpg",Ps="/assets/IMG_6047._T9GjFU6.jpg",Cs="/assets/IMG_6072.mJn83OP1.jpg",ws="/assets/IMG_6073.1Jz6leQz.jpg",Es="/assets/IMG_4146.huBoJH6A.jpg",Ts="/assets/IMG_4148.XrPYzD2y.jpg",Ws="/assets/IMG_4162.UWcsEMxz.jpg",zs="/assets/CAGP8029.djfhCQap.jpg",Bs="/assets/IMG_3918.hSeeiPUH.jpg",Ys="/assets/IMG_3932.7A1DIi8J.jpg",Hs="/assets/IMG_3935.C4o2QX3L.jpg",Js="/assets/IMG_3937.fPPwH55R.jpg",Os="/assets/IMG_3938.IDzZs9Gt.jpg",ys="/assets/IMG_4009.dnpyp5Mb.jpg",Fs="/assets/IMG_5179.TsHn3vZC.jpg",Ls="/assets/IMG_5119.Bd6IAgG6.jpg",Zs="/assets/French-Cleat.QVjxsimF.jpg",Ns="/assets/IMG_5173.Ayz0nYsV.jpg",Rs="/assets/IMG_4041.uDExPouV.jpg",Ss="/assets/IMG_4452.nmtJcMxC.jpg",Vs="/assets/IMG_4453.Q1JZPiQw.jpg",As="/assets/IMG_4052.AzS8dfP9.jpg",Qs="/assets/IMG_4058.yZGEIQNi.jpg",Xs="/assets/IMG_4061.65yr-nOc.jpg",Us="/assets/IMG_4448.SB3jeYK1.jpg",vs="/assets/IMG_4456.-BxbBKY0.jpg",Ks="/assets/IMG_4458.G5NIF1Cd.jpg",$s="/assets/IMG_4463.jyfHrWar.jpg",st="/assets/IMG_4464.k_8hiOCw.jpg",tt="/assets/vKHsg5jaR7c.ZGCD2a9U.jpg",at="/assets/IMG_3903.aFtk2WR6.jpg",pt="/assets/IMG_4598.C5xwQp8z.jpg",_t="/assets/IMG_4606.howglHFN.jpg",rt="/assets/IMG_4615.r9oJsl0z.jpg",ot="/assets/IMG_4619.M8p_XAt-.jpg",ct="/assets/IMG_4620.z8oxGtrE.jpg",it="/assets/IMG_4621.QavD-v5h.jpg",et="/assets/IMG_4622.YO6V86fL.jpg",mt="/assets/IMG_4623.RuChWI-4.jpg",gt="/assets/IMG_4624.wsTnh8Gh.jpg",lt="/assets/IMG_4625.lAXDHiHR.jpg",nt="/assets/IMG_4627.dFSADEmc.jpg",jt="/assets/IMG_4631.x40G91ep.jpg",It="/assets/IMG_4635.ftW4MvZZ.jpg",Gt="/assets/knhILr0kasM.0nVTbIHq.jpg",Mt="/assets/IMG_5183.9Jg0cjR9.jpg",wt=JSON.parse('{"title":"Делаем сами Дом культуры «Делай Сам/а»","description":"","frontmatter":{"title":"Делаем сами Дом культуры «Делай Сам/а»","date":"2016-10-29","cover":"sam.jpg","icon":"logo.png","toc":true},"headers":[],"relativePath":"practice/project/dkds/index.md","filePath":"practice/project/dkds/index.md"}'),ht={name:"practice/project/dkds/index.md"},dt=p('<p>Марафон городских действий «Делай Сам» вырос из небольшой экологической акции до масштаба ежегодных фестивалей городского активизма за каких-то 5 лет. Прекрасная в своей простоте идея самовольного улучшения города в духе DIY объединяет уже действующих активистов и их сообщества, а также тех, кто только знакомится с этим сравнительно новым явлением. Теперь <a href="http://www.delaisam.org/" class="external-link" target="_blank" rel="noreferrer">«Делай Сам»</a> — это открытая платформа, для синхронизации усилий десятков активистов в нескольких городах России и ближнего зарубежья, собственноручно воплощающих свои идеи в жизнь. Мы впервые посетили неформальную конференцию «Делай Саммит» в 2014 году в Культурном центре ЗИЛ, а на следующий год как команда OOLEY приняли активное участие в конференции на территории дизайн-завода «Флакон», где провели несколько мастер-классов по столярке и шитью на базе нашей <a href="/practice/ooley42/">мастерской в прицепе</a>. Мы полностью разделяем решительность в стремлении к самостоятельности, сотрудничеству и творчеству и рады сделать свой вклад в полезное общее дело.</p><blockquote><p>Делай Сам — это не бренд, не фестиваль и не организация. Делай Сам — это идея самовольного улучшения города своими руками. Вам не нужно никаких разрешений, согласований или договоренностей для того, чтобы разделять эту идею и собственноручно сажать цветы, делать лавки, раздельно собирать мусор или ездить на работу на велосипеде.</p></blockquote><p>Группе художников и городских активистов, которые координировались через e-mail-рассылки и социальные сети, постепенно стало не хватать пусть и ежегодных, но всё же временных собраний. Нашлись те, кто всерьёз воспринял эти мысли и решились воплотить их в жизнь: Дарья Федотова и Марийка Семененко придумали Дом Культуры «Делай Сам/а» и взяли на себя инициативу сначала по поиску подходящего помещения, а затем и по организации его обустройства и работы. Несколько недель активных поисков, поездок и переговоров принесли свой результат: нашлось не только пустое складское помещение бывшей типографии, а ныне Центра городской культуры «Правда», но и средства на его ремонт и оплату нескольких месяцев аренды.</p><p>Активистское сообщество откликнулось на призывы к участию и буквально за месяц силами волонтёров бывший склад кофе превратился в просторный светлый зал с синим потолком. Своими силами и инструментами участники команды разбирали старые и возводили новые перегородки, прокладывали провода и трубы, штукатурили и красили стены и потолок. «Делай Саммит» 2016 пришлось немного сдвинуть с традиционно весеннего времени и он состоялся только в конце июня одновременно с открытием ДК «Делай Сам/а». К этому дню были закуплены несколько десятков хороших складных стульев, кто-то принес палеты (какой DIY без них?) и самодельную мебель из ДСП, заработали собранные по знакомым проекторы и колонки, включился кулер с горячей и холодной водой. Для активистов много вещей не нужно: Делай Сам — это в первую очередь люди и их идеи и инициативы. С традиционно насыщенной и разносторонней двухдневной программой новоселье получилось людным даже несмотря на разгар сезона летних отпусков. OOLEY снова принял участие в конференции, где мы продолжили развивать слегка затронутую на предыдущем саммите тему <a href="/practice/project/banner-bags/">шитья из использованных баннеров.</a></p><p><img src="'+_+'" alt=""><img src="'+r+'" alt=""><img src="'+o+'" alt=""><img src="'+c+'" alt=""><img src="'+i+'" alt=""></p><p>Когда ДК открылся, сразу стало очевидно, что его развитие и поддержание потребует ещё много коллективных усилий. Пустой зал с парой перегородок — ещё не Дом культуры. Обустройство было ещё только впереди. Для начала необходимо было придать более чёткую форму размытым представлениям о модели нового для России типа общественных пространств. Мы увидели возможность принять активное участие в реализации смелого культурного проекта, изучить новые навыки и применить на практике собственные умения. Передвижная мастерская OOLEY откликнулась на призывы о помощи в обустройстве и обживании пространства ДК и вскоре мы взялись за первый совместный проект.</p><h2 id="стоика-для-кафе" tabindex="-1">Стойка для кафе <a class="header-anchor" href="#стоика-для-кафе" aria-label="Permalink to &quot;Стойка для кафе&quot;">​</a></h2><p><img src="'+e+'" alt=""><img src="'+m+'" alt=""></p><p>Команда поваров дружественного вегетарианского кафе выразила готовность заняться важным направлением для привлечения гостей и средств на развитие пространства. Они сами закупили и привезли всё необходимое кухонное оборудование, оставалось только придать отведённой под кафе площадке подходящую форму. Выделенная прозрачной перегородкой из поликарбоната зона должна была отделиться от остального пространства высокой стойкой. Даша и Марийка обратились со своей идеей к Ольге Рокаль, архитектору из дружественного <a href="https://www.facebook.com/utroarch" class="external-link" target="_blank" rel="noreferrer">творческого объединения «Утро»</a>,и вскоре получили детальную схему деревянной конструкции из брусков и ОСП. Дальше за дело взялись мы.</p><p><img src="'+g+'" alt=""><img src="'+l+'" alt=""><img src="'+n+'" alt=""><img src="'+j+'" alt=""></p><p>Упаковали передвижную мастерскую в машину, закупили необходимые материалы и позвали всех желающих присоединяться к строительству. С самого утра мы вместе с другими волонтёрами решительно начали пилить бруски 50х50 мм на торцовочной пиле согласно подробной схеме проекта. Собрав основные прямоугольные рамы для стойки и тумбочки, приложили и разметили диагональные укосины, необходимые для придания конструкции жёсткости. Важность этих элементов в конструкциях сложно переоценить, а в оформлении пространства ДК «Делай Сам/а» они стали ещё и одним из заметных элементов дизайна. На готовые рамы саморезами пришили ОСП, покрасив его серой алкидной эмалью. Столешницы вырезали из соснового мебельного щита, который покрыли несколькими слоями тонированного акрилового лака.</p><p><img src="'+I+'" alt=""><img src="'+G+'" alt=""><img src="'+M+'" alt=""><img src="'+h+'" alt=""><img src="'+d+'" alt=""><img src="'+f+'" alt=""></p><p>Только когда пришло время установить стойку на место выяснилось, что размеры помещения при расчётах использовались неверные и стойка оказалась короче проёма на несколько десятков сантиметров. Мы не растерялись, быстро адаптировали проект: скрепили тумбочку с основной стойкой и надёжно прикрепили всю эту конструкцию к колонне и полу. Вдоль дальней стены образовался удобный проход, где оставалось только слегка скруглить углы столешницы. Стойка получилась двухуровневой, причём как изнутри, где появилась дополнительная рабочая поверхность кухни, так и снаружи, где на икеевских консолях подвешен дополнительный столик для перекусов. Вскоре в ДК появились вкусные вегетарианские обеды и индийские сладости. Оставшиеся неиспользованными колёса мы прикрепили к одной из палет и, привязав к ней верёвочную ручку, сделали нехитрую тележку для перемещения тяжестей.</p><p><img src="'+u+'" alt=""><img src="'+b+'" alt=""><img src="'+k+'" alt=""></p><h2 id="витрина-шоу-рума-«переработка»" tabindex="-1">Витрина шоу-рума «Переработка» <a class="header-anchor" href="#витрина-шоу-рума-«переработка»" aria-label="Permalink to &quot;Витрина шоу-рума «Переработка»&quot;">​</a></h2><p><img src="'+x+'" alt=""></p><p>Вторым направлением, которое изначально виделось перспективным источником посетителей и средств, был небольшой центр создания, показа и продажи вещей, сделанных из переработанных материалов в формате апсайклинга, т.е. придания ненужным вещам новых свойств, делающих их полезными в каких-то новых областях применения. От переплавленных и разрезанных стеклянных бутылок до тетрапака и использованных баннеров — многие отходы могут стать подходящим сырьём для создания полезных в повседневности вещей. Повторное использование позволяет сократить не только отходы, но и расходы на производство новых предметов.</p><p><img src="'+q+'" alt=""></p><p>Идея шоу-рума «Переработка» превратилась в новый, чуть более масштабный, архитектурный проект: двухметровый стеллаж и приставная рама у стены с двумя столиками и различными поверхностями для размещения вещей и информации. Часть материалов для конструкции предоставили неравнодушные горожане: в дело пошли подаренные куски ОСП, оставшиеся от какого-то фестиваля, и привезённый с дачи кусок металлической сетки. Понадобилось только пополнить запасы бруса, шурупов и краски. Конечно, такой масштабный проект за один заход сделать сложновато даже при поддержке добровольцев, поэтому на этот раз основную часть конструкции мы запланировали собрать уже в рамках двухдневного открытого воркшопа.</p><p><img src="'+D+'" alt=""><img src="'+P+'" alt=""><img src="'+C+'" alt=""><img src="'+w+'" alt=""><img src="'+E+'" alt=""><img src="'+T+'" alt=""></p><p>С самого утра мы начали пилить торцовочной пилой бруски в размер. Аня и Галя освоили работу с шуруповёртом и взялись собирать рамки для стеллажа с перемычками, к которым мы прикрепили нарезанные в размер полки из ОСП. Вместе мы смело переосмысляли исходный проект на ходу, адаптируя его под имеющиеся условия: отдельные «ноги» стенда мы связали опорным бруском, а саму конструкцию собрали в виде двух практически симметричных рамок с укосинами. Металлическую сетку закрепили, прижав её по периметру рамой из того же бруска. Каркас мы тут же на месте красили белой акриловой краской, а полки покрывали серой алкидкой на свежем воздухе. Краске нужно время чтобы высохнуть, а людям — чтобы набраться сил завершить начатое. Вскоре полностью покрашенные и соединённые друг с другом стеллаж, два столика с полками и стенд мы надёжно прикрепили к стенам и полу дюбель-гвоздями.</p><p><img src="'+W+'" alt=""><img src="'+z+'" alt=""><img src="'+B+'" alt=""><img src="'+Y+'" alt=""></p><p><img src="'+H+'" alt=""><img src="'+J+'" alt=""><img src="'+O+'" alt=""></p><p><img src="'+y+'" alt=""><img src="'+F+'" alt=""></p><p>Нам удалось научиться делать доски для кнопок из фанеры и слоёв тонкой листовой пробки с помощью специального клея, но от размещения такой доски в шоу-руме решили отказаться. Вместо этого мы повесили у входа меловую доску, а рядом, впоследствии, поместили полупрозрачную светящуюся вывеску шоу-рума. Она собрана Аней и Галей из куска ненужного прозрачного пластика и рамки из сосновых реек. Однажды научившись работать с шуруповёртом, не перестаешь открывать для себя все новые конструктивные возможности. Мы аккуратно и ровно наклеили вырезанные на плоттере наклейки с буквами слова «переработка», потом покрыли всю вывеску несколькими слоями чёрной аэрозольной краски, а затем сняли наклейки, открыв прозрачные буквы, которые отлично выглядят при включении задней светодиодной подсветки, которую можно организовать LED-лентами или небольшими светильниками на батарейках.</p><p><img src="'+L+'" alt=""><img src="'+Z+'" alt=""><img src="'+N+'" alt=""><img src="'+R+'" alt=""><img src="'+S+'" alt=""><img src="'+V+'" alt=""><img src="'+A+'" alt=""><img src="'+Q+'" alt=""><img src="'+X+'" alt=""></p><h2 id="столы-и-стеллаж-в-мастерскую" tabindex="-1">Столы и стеллаж в мастерскую <a class="header-anchor" href="#столы-и-стеллаж-в-мастерскую" aria-label="Permalink to &quot;Столы и стеллаж в мастерскую&quot;">​</a></h2><p>Помимо основного зала для встреч и собраний в независимом Доме культуры с самого начала планировалось оборудовать небольшую творческую мастерскую, где художники и и активисты могли бы работать над своими проектами. Для этого в глубине пространства стеной, наполовину из прозрачного поликарбоната, была отгорожена небольшая комната с раковиной и хорошим освещением. На первое время туда принесли громоздкие самодельные столы из тонких листов ДСП, вдоль стены от складской сторожки остался хлипкий узкий стол. Постепенно это пространство стало местом, куда складывается всё, что было принесено в ДК, но не нашло своего места в основном пространстве: куски фанеры и пластика, рекламные щиты и баннеры, баллоны и банки с красками. А ведь ещё нужно где-то хранить инструменты и запасы материалов! Возникла острая необходимость в стеллаже и нормальных рабочих поверхностях. Снова за дело!</p><p><img src="'+U+'" alt=""><img src="'+v+'" alt=""><img src="'+K+'" alt=""><img src="'+$+'" alt=""><img src="'+ss+'" alt=""><img src="'+ts+'" alt=""></p><p>Всё больше проникаясь идеями переработки, мы постарались максимально использовать уже имеющиеся в нашем распоряжении материалы: каркасы мы строили из оставшихся от стойки и стенда брусков, а также из брусков, доставшихся от организаторов летних фестивалей. Собрав рамки из бруса с перемычками, мы связали их полками из обрезков ОСП. Всю конструкцию покрыли слоем акрилового лака и надёжно прикрепили к стене с помощью уголков и дюбель-гвоздей. В мгновение ока полки заполнились ящиками с художественными принадлежностями, инструментами и разнообразными материалами.</p><p><img src="'+as+'" alt=""><img src="'+ps+'" alt=""><img src="'+_s+'" alt=""><img src="'+rs+'" alt=""><img src="'+os+'" alt=""></p><p>Следом пошли столы. Разобранные и распиленные фестивальные конструкции легли в основу крепкого и устойчивого рабочего стола. Три жёсткие рамки мы скрепили парой широких брусков, усилили конструкцию укосинами и накрыли сверху полутора листами фанеры. Шлифовка, тонированный лак — готово! Освоив эту нехитрую технологию, такую мебель вполне возможно делать в одиночку. Так второй стол, сделанный взамен неудобных коробок из ДСП, был сделан силами одного человека меньше чем за день. Во многом такая скорость обусловлена точностью и скоростью работы с торцовочной пилой, ради которых остальным обитателям и посетителям ДК, правда, пришлось потерпеть эти внезапные оглушающе громкие и пыльные запилы. 4 ножки, 4 бруска для подстолья и 8 одинаковых небольших укосин, делающих эту нехитрую конструкцию на удивление жёсткой. Всё та же фанера в качестве столешницы, покрытие всё тем же тонированным лаком. 80 см — подходящая высота для стола, за которым можно работать как сидя, так и стоя. Теперь рабочих поверхностей в мастерской хватит на всех.</p><p><img src="'+cs+'" alt=""></p><h2 id="мобильныи-стол-для-переговоров" tabindex="-1">Мобильный стол для переговоров <a class="header-anchor" href="#мобильныи-стол-для-переговоров" aria-label="Permalink to &quot;Мобильный стол для переговоров&quot;">​</a></h2><p><img src="'+is+'" alt=""></p><p>Следующим важным пунктом в обустройстве ДК стало изготовление длинного стола для встреч и переговоров. Снова готовы архитектурные эскизы, однако отработанный на столах в мастерской опыт подсказывал немного изменить запланированный дизайн. Использование коротких укосин в верхней части ножек стола позволяет избежать использования горизонтальных перемычек в нижней их части: так ничто не мешает ногам и можно спокойно придвинуть стул и расположиться за столом с максимальным удобством.</p><p><img src="'+es+'" alt=""><img src="'+ms+'" alt=""><img src="'+gs+'" alt=""></p><p>Снова грохочем торцовочной пилой: пилим метровые бруски для трёх секций подстолья. Стол для переговоров и работы за компьютером должен иметь высоту чуть выше обеденного, но, конечно, ниже рабочего верстака. При выбранной высоте рабочей поверхности в 75 см, оставляем 5 см на толщину бруса подстолья, 2 см на столешницу и пилим 4 бруска ножек длиной 68 см. Ещё 4 ножки будут с колёсами, так что делаем их короче на высоту колёс, в нашем случае — на 10 см. Целых 20 укосин отпиливаем длиной около 30-35 см. Собираем секции с помощью саморезов, уголками закрепляем ножки на своих местах. Затем каждую из них укрепляем укосинами по каждому направлению. Укосины крепим шурупами, вкручивая их в заранее просверленные под нужным углом отверстия.</p><p><img src="'+ls+'" alt=""><img src="'+ns+'" alt=""><img src="'+js+'" alt=""></p><p>Только когда пришло время установить колёса, выяснилось, что их крепёжная площадка немного больше среза бруса 50х50 мм. Вкручивая шурупы в самые уголки крепления, можно зацепиться за брусок, но такому соединению не хватает надёжности: боковые нагрузки могут легко вырвать крепление. Опыт исследований в области переработки и творческого использования вторичного сырья подсказал отличный способ укрепить слабые места. Всё гениальное просто: мы натянули на места присоединения площадок колёс цилиндры из обрезанных полуторалитровых пластиковых бутылок, которые затем нагрели с помощью технического фена. Нагреваясь, ПЭТ значительно сжимается, надёжно стягивая всю конструкцию, а также становится более прочным и жёстким, что ещё больше усилило место крепежа. Осталось традиционно покрасить бруски в белый цвет, а столешницу из 12 мм фанеры покрыть серой акриловой краской и тремя слоями глянцевого акрилового лака. Стол получился крепкий и устойчивый, но при этом настолько лёгкий, что практический каждый сможет перекатить его в любой угол зала в одиночку.</p><p><img src="'+Is+'" alt=""><img src="'+Gs+'" alt=""></p><h2 id="стол-из-бумажных-стаканчиков" tabindex="-1">Стол из бумажных стаканчиков <a class="header-anchor" href="#стол-из-бумажных-стаканчиков" aria-label="Permalink to &quot;Стол из бумажных стаканчиков&quot;">​</a></h2><p>Ворота ДК «Делай Сам/а» выходят во внутренний двор ЦГК «Правда», где в обычные дни паркуют свои машины работники и посетители многочисленных музыкальных, фото- и тату-студий, танцевальных залов и многих других заведений. Иногда эту парковку освобождают от машин и используют как площадку для проведения различных городских фестивалей. Организаторы планировавшегося здесь пивного фестиваля прониклись идеями переработки и предложили команде ДК принять участие в событии. Изучая возможные варианты сотрудничества, мы нашли и развили идеи DIY-переработки бумажных одноразовых стаканчиков, которые на таких событиях расходуются в огромных количествах. Мы задумали создать сразу два крупных объекта из использованной бумажной тары, при этом приглашая всех желающих поучаствовать во всех этапах их воплощения.</p><p><img src="'+Ms+'" alt=""><img src="'+hs+'" alt=""><img src="'+ds+'" alt=""><img src="'+fs+'" alt=""></p><p>Задумка была следующая: собрать, скрепляя стаканы без донышек степлером, большой шар — люстру, а также высокий стол-стойку из послойно приклеенных к тонкой фанере стаканчиков. Мы закупили несколько листов фанеры, по несколько недорогих термоклеевых пистолетов и степлеров, а также запас стержней и скоб к ним. Пока фестиваль ещё готовился открыть свои двери для горожан, мы вместе с волонтёрами события протестировали наши задумки и сделали первые шаги по их реализации: вырезали из фанеры форму будущей стойки и наклеили первые полтора ряда стаканов, а также скрепили большую часть шара. Остальную работу мы предложили проделать посетителям праздника. Для этого мы оборудовали бочку со шлангом для мытья стаканов и запас сухих тряпок для их последующего вытирания (влага может покоробить фанеру, а термоклей не склеивает влажные поверхности). Многие гости фестиваля с радостью откликнулись на наш призыв и ряд за рядом стол начал расти. Сухой стаканчик ставится на фанеру, затем пистолетом равномерно по всей окружности приклеивается к ней термоклеем. Когда слой готов, он накрывается ещё одним куском фанеры и приклеивается к нему по периметру.</p><p><img src="'+us+'" alt=""><img src="'+bs+'" alt=""><img src="'+ks+'" alt=""><img src="'+xs+'" alt=""><img src="'+qs+'" alt=""></p><p>Мы задумали сделать не просто письменный стол, а высокую стойку администратора ДК, высота которой примерно 80 см, что потребовало целых 7 слоёв стаканов. Конечно, усилий отдельных добровольцев было недостаточно, но мы смогли организовать сбор, сортировку и мытьё стаканов из мусорных баков, расставленных по территории фестиваля. Мыли, вытирали и клеили по-очереди все вместе. Стемнело, но мы были уверены, что справимся и закончим стол, пусть и к самому закрытию фестиваля. Так и получилось. Неустанными совместными усилиями нам удалось не только успеть собрать из мусора полезный элемент мебели, но даже покрасить его аэрозольными красками и установить в ДК неподалёку от входа. Конструкция получилась вполне устойчивой и прочной. Столешницу мы сделали из чуть более толстой фанеры слегка иной формы, покрасили её чёрным акрилом и покрыли парой слоёв матового лака. Так гости фестиваля получили новый необычный опыт экологически ответственного праздненства, а ДК|ДС обзавёлся новым полезным элементом мебели.</p><p><img src="'+Ds+'" alt=""><img src="'+Ps+'" alt=""><img src="'+Cs+'" alt=""><img src="'+ws+'" alt=""></p><h2 id="мебель-в-мастерскую-из-ящиков-от-стеклотары" tabindex="-1">Мебель в мастерскую из ящиков от стеклотары <a class="header-anchor" href="#мебель-в-мастерскую-из-ящиков-от-стеклотары" aria-label="Permalink to &quot;Мебель в мастерскую из ящиков от стеклотары&quot;">​</a></h2><p><img src="'+Es+'" alt=""><img src="'+Ts+'" alt=""><img src="'+Ws+'" alt=""></p><p>Переработка пивного фестиваля принесла ДК не только удобный стол. На следующий день мы увидели на помойке целую кучу пластиковых ящиков, использовавшихся для создания импровизированной фестивальной мебели. Эти ящики уже один раз претерпели превращение в мебель, а мы придумали, как их применить и у нас. Ставим один перевёрнутый ящик на другой, скрепляем их по углам саморезами, а сверху накрываем дно вырезанным по форме куском тонкой фанеры. Крепим её шурупами, покрываем краской или лаком — табурет готов. Высота такой стопки ящиков слегка высоковата для сидения за обеденным столом, но она идеально подошла к нашим рабочим столам. Мы принесли целую гору ящиков и собрали 8 табуретов, которые аккуратно убираются под столы и всегда готовы не только к удобному размещению участников воркшопов, но и могут служить дополнительной рабочей поверхностью. Оставшиеся ящики мы скрепили торцами с помощью нейлоновых стяжек и, установив под небольшим углом, превратили в стойку для удобного хранения баллонов с аэрозольной краской и лаками.</p><h2 id="места-сидения" tabindex="-1">Места сидения <a class="header-anchor" href="#места-сидения" aria-label="Permalink to &quot;Места сидения&quot;">​</a></h2><p><img src="'+zs+'" alt=""><img src="'+Bs+'" alt=""><img src="'+Ys+'" alt=""></p><p>Лёгкие складные стулья идеально подходят для рассаживания рядами зрителей, работы за длинным столом и перекуса у стойки кафе. Но этим возможности размещения для гостей ДК не ограничиваются. Кому-то захочется присесть у высокого стола-стойки, а летом всех так и тянет побыть подольше на улице. Мы ищем вдохновение в сети и в мусоре, находя новые идеи по переработке ненужных вещей в общественную мебель. Неравнодушные горожане принесли в ДК элементы мебели, мы нашли способы их переработать. Например, стул без сидения мы восстановили, вырезав кусок ОСП нужной формы и обтянув его яркой тканью, тоже переданной ДК в качестве дара.</p><p>Не всегда всё так просто. Чтобы сделать ещё один высокий барный стул, мы взяли за основу мягкое сиденье со спинкой из кожзама. Для него мы с помощью ручной пилы, стусла и шуруповёрта собрали из брусков крепкую опору, покрасили её белым акрилом и прикрепили к сиденью пластиковыми хомутами. Кому-то такая мебель может показаться непрезентабельной, но она отлично передаёт утилитарный дух DIY-движения. Из деревянного ящика от фруктов те же инструменты помогли собрать небольшую табуретку. Пару мягких компьютерных кресел, которые кто-то вынес на помойку «Правды» было достаточно просто перекрасить и они сразу нашли своё место в библиотеке ДК. Конечно, такие решения нельзя считать долгосрочными, но умение находить возможности оперативно решать возникающие задачи практически без денег — действенный способ развития некоммерческого волонтёрского проекта в условиях современного мегаполиса.</p><p><img src="'+Hs+'" alt=""><img src="'+Js+'" alt=""><img src="'+Os+'" alt=""><img src="'+ys+'" alt=""></p><p>Поставив ещё один деревянный поддон на колёса, мы нарастили его высоту двумя половинками палеты, а посередине сделали общую спинку, сформировав передвижную скамейку, на которой приятно посидеть на уличном пандусе летним вечерком. Вместе мы придумали расцветку и покрасили всё аэрозольными красками. Для удобства пришлось взять в руки «болгарку» с шлифовальным диском и сточить острый край поддона, упиравшийся в спину. Такое скругление сделало скамейку вполне удобной.</p><p><img src="'+Fs+'" alt=""><img src="'+Ls+'" alt=""></p><p>Когда для кафе в ДК понадобились дополнительные столики, стало ясно, что из имеющихся обрезков и находок сделать что-то достаточно лёгкое и аккуратное не получится и мы предложили использовать для этих целей комплект для сборки, изготовленный нами в столярной мастерской. Мы привезли в ДК набор <a href="/practice/project/stol-x/">«Стол-Х»</a>, состоящий из 6 деталей ножек из лиственницы, 6 сосновых досок столешницы и подготовленных к расклёпке 4 алюминиевых втулок. В таком виде стол при перевозке занимает очень мало места, а сборка его до готовности производится за 15—20 минут. Молоток и стальной боёк для расклёпки быстро соединяют каркас, к которому шуруповёртом притягиваем столешницу. Стабилизируем конструкцию перемычками внизу и остаётся только покрыть все это матовым лаком — складной столик для кафе готов.</p><h2 id="хранение-мелочевки-в-мастерскои" tabindex="-1">Хранение мелочёвки в мастерской <a class="header-anchor" href="#хранение-мелочевки-в-мастерскои" aria-label="Permalink to &quot;Хранение мелочёвки в мастерской&quot;">​</a></h2><p>Деревянный стеллаж в мастерской помог найти место имеющимся инструментам и коробкам с материалами. Полка на кронштейнах над рабочим столом дала место для хранения рулонных и листовых материалов. Но встал вопрос о хранении небольших инструментов вроде отвёрток и плоскогубцев, а также всевозможных шурупов, винтов, гаек и других полезных мелочей. Исследуя варианты простых и дешёвых решений, мы остановились на использовании удобной системы «французский клин» (<a href="https://en.wikipedia.org/wiki/French_cleat" class="external-link" target="_blank" rel="noreferrer">&quot;french cleat&quot;</a>).</p><p><img src="'+Zs+'" alt=""></p><p>Для такого крепления достаточно распилить доску вдоль циркулярной пилой с лезвием, установленным под углом 45°. Так получаются две детали, идеально совмещающиеся друг с другом и способные надёжно удерживать даже значительный вес. Для этой системы мы отвели стену над длинным столом в мастерской. Стена там сделана из гипсокартона и для надёжного крепления опорных планок понадобилось использовать довольно много шурупов. Мы не планируем вешать на эти планки какие-то тяжёлые полки, а для мелочёвки запаса прочности должно хватить. Ответную часть доски мы распилили на множество небольших кусочков, каждый из которых стал основой для создания отдельных единиц хранения. Основную массу таких модулей мы сделали из обрезанных пластиковых бутылок. Треугольные обрезки от укосин пошли в ход как уголки при сборке полки с отверстиями для отвёрток и ножниц. Вкручиваем пару саморезов в ответную планку и получаем модуль для подвеса молотка. Присоединив на клей и шурупы фанерные площадки и обрезки реек, мы сделали модули для хранения множества видов клейких лент, незаменимых в мастерской. Удобно разместив основную массу мелочёвки на стене, мы продолжаем пополнять систему новыми модулями по мере необходимости.</p><p><img src="'+Ns+'" alt=""><img src="'+Rs+'" alt=""><img src="'+Ss+'" alt=""><img src="'+Vs+'" alt=""><img src="'+As+'" alt=""><img src="'+Qs+'" alt=""><img src="'+Xs+'" alt=""></p><h2 id="дзен-гардероб" tabindex="-1">Дзен-гардероб <a class="header-anchor" href="#дзен-гардероб" aria-label="Permalink to &quot;Дзен-гардероб&quot;">​</a></h2><p>Лето в Москве довольно короткое и уже в сентябре погода стала по-настоящему осенней. Наступило время курток, зонтов и дождевиков. Понятно, что всё это гостям ДК нужно куда-то вешать, пришла пора обустраивать гардероб. Мы давно обсуждали варианты его реализации: рассматривали идеи с множеством настенных крючков и подвесными системами. Но когда пришло время воплощать идеи в жизнь, мы увидели куда более простые и дешевые альтернативы. В магазине нужно было купить только трёхметровую профильную стальную трубу, а все остальное нашлось в мастерской. Скруглив две грани бруска рубанком, мы просверлили в одной из них ряд отверстий, в которые вбили готовые деревянные шканты. Покрыли вешалку лаком и прикрутили шурупами с обратной стороны перегородки из ОСП. Для крепления к стене стальной штанги мы выпилили из обрезков широких брусков две колодки с пазами по размеру трубы. Закрепив их к стойке и стене шурупами, вставлили в пазы предварительно загрунтованную и покрашенную белой аэрозольной краской штангу. Простая, но очень вместительная гардеробная система готова принять на себя вес нескольких десятков курток и пальто. Главное — не испытывать штангу подтягиваниями.</p><p><img src="'+Us+'" alt=""><img src="'+vs+'" alt=""><img src="'+Ks+'" alt=""><img src="'+$s+'" alt=""><img src="'+st+'" alt=""><img src="'+tt+'" alt=""></p><p>Также чтобы более чётко отделить зону кафе от гардероба и двери, ведущей в туалет, мы возвели новую полупрозрачную перегородку из двух подаренных кем-то ДК дубовых палет. Отшлифовав старые загрязнения, мы увидели красивую живую текстуру этого благородного дерева и решили оставить его без отделки. Закрепив первый поддон дюбель-гвоздями к полу и стене, мы водрузили на него второй и скрепили их друг с другом. Без усиления дополнительной стойкой внешнего края перегородка получалась довольно хлипкой и мы использовали доски оставшиеся от фестивальных конструкций для придания стенке необходимой прочности. В итоге мы получили крепкую полупрозрачную стену с красивой текстурой дерева, которая эффективно делит пространство на зоны без образования тёмных углов. Гардероб готов встречать гостей.</p><h2 id="пластиковое-окно-в-проем-гаражнои-двери" tabindex="-1">Пластиковое окно в проём гаражной двери <a class="header-anchor" href="#пластиковое-окно-в-проем-гаражнои-двери" aria-label="Permalink to &quot;Пластиковое окно в проём гаражной двери&quot;">​</a></h2><p>Наиболее важный этап подготовки пространства к зиме был ещё впереди. Широкая гаражная дверь — символ открытости и гостеприимства ДК «Делай Сам/а». Но если летом она приносит свежий воздух и приятную прохладу, то уже в начале осени стало ясно, что она довольно плохо защищает от холода, а через щель снизу в помещение ощутимо дует ветер. Полностью закрывать и герметизировать дверь — значит потерять связь пространства с улицей, оставив людям снаружи лишь небольшую вывеску и чёрную железную дверь. Было решено сделать большое окно.</p><p><img src="'+at+'" alt=""><img src="'+pt+'" alt=""><img src="'+_t+'" alt=""><img src="'+rt+'" alt=""><img src="'+ot+'" alt=""><img src="'+ct+'" alt=""><img src="'+it+'" alt=""></p><p>Стекло как материал мы даже не рассматривали, а обратились к опыту <a href="/workshop/catalog/surf2free/">нижегородского сёрф-лагеря «FreeDOM»</a>, где мы недавно побывали. Там мы видели множество окон, собранных из двух слоёв прозрачного листового пластика на утеплённой раме. Рассмотрев различные виды пластика, мы остановились на применении намного более прочного, чем оргстекло, и более стойкого к внешним погодным условиям, чем ПЭТ, монолитного поликарбоната и заказали два листа 2050х3000 мм толщиной 3 мм в компании, производящей теплицы. Т.к. проём двери оказался немного выше, чем 2050 мм, нам пришлось придумывать способ приподнять всю конструкцию окна без потери прочности и теплоизоляционных свойств. Решением стало использование в качестве основания большого клеенного бруса 100х100 мм. Также нам понадобились ещё 6 брусков 50х50 мм для рамы, самоклеющаяся резиновая уплотнительная лента, саморезы для сборки рамы и крепления пластика, а также уголки и дюбель-гвозди для прикрепления рамы к полу и стенкам проёма.</p><p><img src="'+et+'" alt=""><img src="'+mt+'" alt=""><img src="'+gt+'" alt=""><img src="'+lt+'" alt=""><img src="'+nt+'" alt=""><img src="'+jt+'" alt=""><img src="'+It+'" alt=""><img src="'+Gt+'" alt=""></p><p>Срезав все выступающие части металлической рамы, мы напилили бруски в размер проёма с помощью обычного пластикового стусла и ручной пилы. Готовые элементы конструкции мы тут же на месте красили белой акриловой краской, просушивая её тепловентилятором. Одновременно с помощью электролобзика с пилкой по металлу на малой скорости напилили в размер поликарбонат.  Затем закрепили клеенный брус в основании, собрали раму в проёме. Наклеив уплотнительную ленту по всему периметру рамы, прислонили и закрепили шурупами через каждый 20-25 см внешний слой поликарбоната. Перед тем, как закрыть раму вторым слоем пластика, насыпали в неё немного обычной поваренной соли, которая сможет впитать всю излишнюю влагу и предотвратит запотевание окна. После установки второго листа поликарбоната надёжно крепим всю конструкцию к металлической раме проёма, заполняем все оставшиеся щели утеплителем и монтажной пеной и любуемся получившейся картинкой. Днём солнечный свет по-прежнему заливает всю входную группу ДК, а по вечерам тёплое освещение внутренних помещений мягко струится наружу, приоткрывая внутренний мир пространства взглядам прохожих. Такое окно — не просто символ, это декларация прозрачности и открытости проекта людям. К тому же с ним тепло и тихо, ну и видно тех, кто есть внутри и кто только собирается зайти.</p><p><img src="'+Mt+'" alt=""></p><h2 id="продолжение-следует" tabindex="-1">Продолжение следует <a class="header-anchor" href="#продолжение-следует" aria-label="Permalink to &quot;Продолжение следует&quot;">​</a></h2><p>Начавшись с отдельных проектов, работа в ДК превратилась в настоящий поток разнообразных творческих начинаний. Обустраивая и «обживая» пространство,  мы получили массу опыта в конструировании простой, но функциональной мебели, а также погрузились в практику тотальной «переработки». Сотрудничая с разными людьми по различным направлениям, мы смогли реализовать проекты, невозможные для каждого из нас по отдельности. Усилиями добровольцев при минимальном вложении денег пустой склад бывшей типографии превратился в общественный центр, бросающий вызов существующей городской культуре и вдохновляющий активистов выходить из тени, общаться и объединять усилия для улучшения города и жизни в нём. ДК «Делай Сам/а» — настоящий социальный эксперимент, где никто не знает результаты наперёд. Каждый может подключиться и внести свой вклад в развитие проекта, и не мы одни считаем, что это того стоит. Не ждать указаний и не просить разрешений, а своими руками превращать окружающий мир в более прекрасное место — начать бывает сложно, но продолжать так действовать всегда невероятно интересно. Главное — пробовать, внимательно наблюдать и быть открытыми к любым изменениям. А лучше — стать этими изменениями самим.</p><p>Мы постараемся и дальше смело идти вперёд, смотря в лицо возникающим трудностям и неопределённости, и, конечно, продолжим делиться наработками с вами. До встречи в ДК «Делай Сам/а»!</p>',78),ft=[dt];function ut(bt,kt,xt,qt,Dt,Pt){return t(),a("div",null,ft)}const Et=s(ht,[["render",ut]]);export{wt as __pageData,Et as default};
